$(function(){
	//  
	$('#header .btn-m').click(function(){
		$('body').addClass('aside-on');
		return false;
	})
	$('.aside .btn-close, .bg-aside').click(function(){
		$('body').removeClass('aside-on');
		return false;
	})
	
	if($('.scrollbar-inner').length > 0){
		$('.scrollbar-inner').scrollbar();
	}
	//   
	$('.btn-cinema-find').click(function(){
		$('body').toggleClass('cinema-on');
		$(this).toggleClass('active');
		$('.cinema-find-wrap').fadeToggle(300);
		return false;
	});
	
	$('.cinema-find-wrap .btn-close').click(function(){
		$('body').removeClass('cinema-on');
		$('.btn-cinema-find').toggleClass('active');
		$('.cinema-find-wrap').fadeOut(300);
		return false;
	});
	
	$('.reservation-pc .body .list-head .btn-search').click(function(){
		$(this).next().fadeIn(300);
		return false;
	})
	$('.reservation-pc .body .list-head .btn-remove').click(function(){
		$(this).closest('.search').fadeOut(300);
		return false;
	});
	
	$('.reservation-pc .head .right .btn-close').click(function(){
		$(this).closest('.reservation-pc').fadeOut(300);
		$(this).closest('.reservation-pc').prev().fadeOut(300);
		return false;
	});
	
	//  
	if($('.schedule-slider').length > 0 ){
		var scheduleSwiper = new Swiper('.schedule-slider .swiper-container', {
			slidesPerView: 'auto',
			spaceBetween: 10,
			navigation: {
				nextEl: '.schedule-slider .swiper-button-next',
				prevEl: '.schedule-slider .swiper-button-prev',
			},
			breakpoints: {
				767: {
				  slidesPerView: '8',
				  spaceBetween: 10,

				}
			  }
		});
	}
	
	//  
	if($('.partner').length > 0 ){
		var scheduleSwiper = new Swiper('.partner .swiper-container', {
			slidesPerView: '2',
			spaceBetween: 10,
			breakpoints: {
				1023: {
				  slidesPerView: '4',
				  spaceBetween: 40,

				}
			  }
		});
	}
	
	$('.board-head .srch-box .btn-del').click(function(){
		$(this).prev('.inp').val('');
		$(this).hide();
		$(this).next('.btn-srch').click();
	});
	$('.board-head .srch-box .inp').on('input', function(){
		if($(this).val() == ""){
			$(this).siblings('.btn-del').hide();
		}else{
			$(this).siblings('.btn-del').show();
		}
	});
	
	// 20220819 
	$('#footer .btn-flip').click(function(){
		$(this).toggleClass('active');
		$('#footer .addr').stop().slideToggle(300);
		return false;
	});
	
	$('#BtnShareOpen').click(function(){
		$('.bg-sns').fadeIn(300);
		$('.sns-layer').fadeIn(300);
		return false;
	});
	$('.bg-sns, .sns-layer .btn-close').click(function(){
		$('.bg-sns').fadeOut(300);
		$('.sns-layer').fadeOut(300);
	});

});
